const factor = 100

function rand (variety, minimum) {
  return Math.ceil(Math.random() * variety * factor) + (minimum * factor)
}

function health () {
  return rand(25, 25)
}

function build () {
  return rand(5, 10)
}

function attack () {
  return rand(10, 5)
}

module.exports = { health, build, attack }
